import React from 'react';
import "./Card.scss"
import Button from '../Button/Button';
import PropTypes from "prop-types";
import Modal from '../Modal/Modal';



const Card = (props) => {

    const { card, } = props;

    const { header, price, article, src, color, setIsModalActive } = card



    return (
        <li className='card' >
            <h2 className='card__header'>{header}</h2>
            <img src={src} alt={header} width={250} height={250} />
            <div className="card__info">
                <span className='card__prise' >{price}</span>
                <span className='card__article' >art:{article}</span>
                <span className='card__color' style={{ backgroundColor: color }}></span>
            </div>
            <Button handleClick={() => { setIsModalActive(true) }} >Add to Cart</Button>
        </li>
    )
}

Card.propTypes = {
    card: PropTypes.object.isRequired,
    setIsModalActive: PropTypes.func

}

export default Card
