import React from 'react';
import Button from '../Button/Button';
import "./CartItem.scss"
import PropTypes from "prop-types";
import { dicrementAC, incrementAC, removeFromCartAC } from '../../store/cart/actionCreators';
import { useDispatch } from 'react-redux';
import { addModalPropsAC, setIsModalActivelAC } from '../../store/modal/actionCreators';



const CartItem = (props) => {

    const dispatch = useDispatch()

    const { article, src, header, count, } = props

    const actionsForModal =
        <>
            <Button handleClick={() => { dispatch(removeFromCartAC(article)); dispatch(setIsModalActivelAC(false)); }}>Delete</Button>
            <Button handleClick={() => { dispatch(setIsModalActivelAC(false)) }}>Cancel</Button>
        </>

    const modalProps = {
        title: `Delete ${header}from cart?`,
        closeButton: true,
        actions: actionsForModal,
    }

    const openModal = () => {
        addModalPropsAC(modalProps)
        setIsModalActivelAC(true)
    }

    openModal()


    return (

        <li className='cart__item' >
            <img src={src} alt={header} width='100' height={100} />
            <h2 className='cart__header' >{header}</h2>
            <span className='cart__count' >{count}</span>
            <Button handleClick={() => { incrementAC(article) }} >+</Button>
            <Button handleClick={() => { dicrementAC(article) }}>-</Button>
            <Button handleClick={() => { openModal() }}>DEL</Button>
        </li>


    );
}

CartItem.propTypes = {
    article: PropTypes.string.isRequired,
    src: PropTypes.string.isRequired,
    header: PropTypes.string.isRequired,
    count: PropTypes.number.isRequired,

};


export default CartItem;
