import { SET_IS_MODAL_ACTIVE, ADD_MODAL_PROPS } from "./actions"

export const closeModalAC = (payload) => ({ type: SET_IS_MODAL_ACTIVE, payload })
export const addModalPropsAC = (payload) => ({ type: ADD_MODAL_PROPS, payload })
