    // }, []);

    return (
        <div className="App">
            <Header cartCount={cartCount} />
            <AppRoutes />
            {/* <CardList addToFavorite={addToFavorite} setModalProps={setModalProps} setIsModalActive={setIsModalActive} data={data} /> */}
            {/* {isModalActive && <Modal addToCart={addToCart} setIsModalActive={setIsModalActive} modalProps={modalProps} />} */}
        </div>
    );
}

export default App;
