import React from 'react';
import Button from '../Button/Button';
import { Form, Formik } from 'formik';
import CustomInput from '../CustomInput/CustomInput';


const CreateOrderForm = () => {

    const initialValues = {
        name: '',
        lastname: '',
        age: '',
        adress: '',
        phone: '',
    }

    return (



        <Formik
            initialValues={initialValues}
        >
            {({ isValid }) => {
                return (<Form>
                    <CustomInput
                        type="text"
                        name="name"
                        placeholder="Name" />
                    <CustomInput
                        type="text"
                        name="lastname"
                        placeholder="Lastname" />
                    <CustomInput
                        type="text"
                        name="age"
                        placeholder="Age" />
                    <CustomInput
                        type="text"
                        name="adress"
                        placeholder="Adress" />
                    <CustomInput
                        type="text"
                        name="phone"
                        placeholder="Phone" />
                    <Button handleClick={() => { }} type="submit" disabled={isValid} >Submit</Button>
                </Form>)
            }}

        </Formik>
    );
}



export default CreateOrderForm;
