import './App.css';
import Header from './components/Header/Header';
import React, { useEffect, useState } from 'react';
import CardList from './components/CardList/CardList';
import Modal from './components/Modal/Modal';



function App() {

    const [data, setData] = useState([])
    const [isModalActive, setIsModalActive] = useState(false)
    const [modalProps, setModalProps] = useState({})
    const [cart, setCart] = useState(localStorage.getItem("cart") || [])
    const [favorite, setFavorite] = useState(localStorage.getItem("favorite") || [])
    const [cartCount, setCartCount] = useState(localStorage.getItem("cartCount") || [])


    const addToCart = (card) => {
        const index = cart.findIndex(el => el.article === card.article);

        if (index === -1) {

            card.count = 1

            const newCart = [...cart, card]


            setCart(newCart)
        } else {
            cart[index].count += 1;

        }

        const newCartCount = cart.reduce((sum, current) => +sum + current.count, 0);
        setCartCount(newCartCount)

        localStorage.setItem("cart", JSON.stringify(cart));
        localStorage.setItem("cartCount", JSON.stringify(cartCount));
    }


    useEffect(() => {
        const getData = async () => {
            const data = await fetch("./data.json").then(res => res.json())
            setData(data)
        }
        getData()
    }, []);

    return (
        <div className="App">
            <Header />
            <CardList setModalProps={setModalProps} setIsModalActive={setIsModalActive} data={data} />
            {isModalActive && <Modal addToCart={addToCart} setIsModalActive={setIsModalActive} modalProps={modalProps} />}
        </div>
    );
}

export default App;
