import React from 'react';
import Card from '../Card/Card';

import "./CardList.scss"
import PropTypes from "prop-types";



const CardList = (props) => {

    const { data, setModalProps, setIsModalActive, addToFavorite, } = props

    return (
        <main>
            <ul className='card-list'>
                {data.map((el) => <Card isFavorite={JSON.parse(localStorage.getItem("favorite").includes(el.article))} addToFavorite={addToFavorite} setModalProps={setModalProps} setIsModalActive={setIsModalActive} key={el.article} card={el} />)}
            </ul>
        </main>
    )
}

CardList.propTypes = {
    data: PropTypes.arrayOf(PropTypes.object).isRequired,
};



export default CardList
