import React from 'react';
import "./Button.scss"
import PropTypes from "prop-types";


const Button = (props) => {

    const { children, handleClick, type, color } = props;


    return (
        <button type={type} style={{ backgroundColor: color }} className='button' onClick={handleClick} >{children}</button>
    )
}

Button.propTypes = {
    children: PropTypes.string,
    handleClick: PropTypes.func.isRequired,
    type: PropTypes.string,
}

Button.defaultProps = {
    children: "Button",
    type: 'button'
}



export default Button
