import { ADD_TO_CART, SET_CART_COUNT, INCREMENT, DICREMENT } from "./actions";
import produce from 'immer';


const initialState = {
    cart: JSON.parse(localStorage.getItem("cart")) || [],
    cartCount: JSON.parse(localStorage.getItem("cartCount")) || 0,
}

const dataReducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_TO_CART: {
            return produce(state, draftState => {

                const index = draftState.cart.findIndex(el => el.article === action.payload.article)
                const currentCard = { ...action.payload }

                if (index === -1) {

                    currentCard.count = 1
                    draftState.cart.push(currentCard);

                } else {

                    draftState.cart[index].count += 1;
                }

                localStorage.setItem("cart", JSON.stringify(draftState.cart));

            });
        }

        case SET_CART_COUNT: {
            return produce(state, draftState => {

                const newCartCount = draftState.cart.reduce((sum, current) => sum + current.count, 0);

                draftState.cartCount = newCartCount;

                localStorage.setItem("cartCount", JSON.stringify(newCartCount));

            })

        }

        case INCREMENT: {
            return produce(state, draftState => {
                const currentCart = draftState.cart
                const idx = currentCart.findIndex(el => el.article === action.payload)

                currentCart[idx].count += 1

                draftState.cart = currentCart;

                const newCartCount = draftState.cart.reduce((sum, current) => sum + current.count, 0);
                localStorage.setItem("cart", JSON.stringify(currentCart));
                localStorage.setItem("cartCount", JSON.stringify(newCartCount));




            })

        }

        case DICREMENT: {
            return produce(state, draftState => {
                const currentCart = draftState.cart
                const idx = currentCart.findIndex(el => el.article === action.payload)

                currentCart[idx].count -= 1

                draftState.cart = currentCart;

                const newCartCount = draftState.cart.reduce((sum, current) => sum + current.count, 0);
                localStorage.setItem("cart", JSON.stringify(currentCart));
                localStorage.setItem("cartCount", JSON.stringify(newCartCount));




            })

        }
        default: {
            return state
        }
    }
}

export default dataReducer
