import { ADD_TO_FAVORITE } from "./actions";

import produce from 'immer';


const initialState = {
    favorite: JSON.parse(localStorage.getItem("favorite")) || [],
    isFavorite: false,
}

const favoriteReducer = (state = initialState, action) => {

    switch (action.type) {

        case ADD_TO_FAVORITE: {

            return produce(state, draftState => {

                const currentFavorite = draftState.favorite
                const article = action.payload

                if (currentFavorite.includes(article)) {

                    const index = currentFavorite.indexOf(article);
                    currentFavorite.splice(index, 1);

                } else {

                    currentFavorite.push(article);

                }

                draftState.isFavorite = !draftState.isFavorite
                draftState.favorite = currentFavorite;
                localStorage.setItem('favorite', JSON.stringify(currentFavorite));
            })

        }

        default: {

            return state

        }
    }
}

export default favoriteReducer
