import React from 'react';
import Button from '../Button/Button';

const CartItem = (props) => {

    const { article, src, header, count, setCart, cartArr } = props

    const dicrement = () => {
        const currentCart = cartArr
        const idx = currentCart.findIndex(el => el.article === article)

        currentCart[idx].count += 1

        setCart(currentCart)




    }

    return (

        <li className='cart__item' >
            <img src={src} alt={header} />
            <h2 className='cart__header' >{header}</h2>
            <span className='cart__count' >{count}</span>
            <Button handleClick={dicrement} >+</Button>
            <Button>-</Button>
            <Button >DEL</Button>

        </li>


    );
}

export default CartItem;
