import './App.css';
import Header from './components/Header/Header';
import React, { useEffect, useState } from 'react';
import Modal from './components/Modal/Modal';
import AppRoutes from './AppRoutes';
import { useDispatch, useSelector } from 'react-redux';
import { getDataAC } from './store/data/actionCreators';
import { addToCartAC, setCartCountAC, incrementAC, dicrementAC, removeFromCartAC } from './store/cart/actionCreators';
import { addToFavoriteAC } from './store/favorite/actionCreators';
import { addModalPropsAC, setIsModalActivelAC } from './store/modal/actionCreators';




function App() {

    const dispatch = useDispatch()
    const data = useSelector((state => state.data.data))
    const cart = useSelector((state => state.cart.cart))
    const cartCount = useSelector((state => state.cart.cartCount))
    const favorite = useSelector((state => state.favorite.favorite))
    const isFavorite = useSelector((state => state.favorite.isFavorite))
    const modalProps = useSelector((state => state.modal.modalProps))
    const isModalActive = useSelector((state => state.modal.isModalActive))


    const addToFavorite = (article) => {
        dispatch(addToFavoriteAC(article))
    }


    const closeModal = () => {
        dispatch(setIsModalActivelAC(false))
    }

    const addToCart = (card) => {
        dispatch(addToCartAC(card))
        dispatch(setCartCountAC(card))
    }

    const dicrement = (article) => {
        dispatch(dicrementAC(article))

    }

    const increment = (article) => {
        dispatch(incrementAC(article))
    }

    const removeFromCart = (article) => {
        dispatch(removeFromCartAC(article))
    }

    useEffect(() => {
        dispatch(getDataAC())
    }, []);

    return (
        <div className="App">
            <Header />
            <AppRoutes />
            {isModalActive && <Modal closeModal={closeModal} modalProps={modalProps} />}
        </div>
    );
}

export default App;
