import './App.css';
import Header from './components/Header/Header';
import React, { useEffect, useState } from 'react';
import CardList from './components/CardList/CardList';
import Modal from './components/Modal/Modal';



function App() {

    const [data, setData] = useState([])
    const [isModalActive, setIsModalActive] = useState(false)
    const [modalProps, setModalProps] = useState({})
    const [cart, setCart] = useState(JSON.parse(localStorage.getItem("cart")) || [])
    const [favorite, setFavorite] = useState(JSON.parse(localStorage.getItem("favorite")) || [])
    const [cartCount, setCartCount] = useState(JSON.parse(localStorage.getItem("cartCount")) || [])
    const [isFavorite, setIsFavorite] = useState(false);



    changeFavorite = (article) => {
        const { favorite } = this.state;
        if (favorite.includes(article)) {
            const index = favorite.indexOf(article);
            favorite.splice(index, 1);
        } else {
            favorite.push(article);
        }
        this.setState({ favorite: favorite });
        localStorage.setItem('favorite', favorite);
    }




    const addToFavorite = (article) => {
        const currentFavorite = favorite
        if (currentFavorite.includes(article)) {
            const index = currentFavorite.indexOf(article);
            currentFavorite.splice(index, 1);
        } else {
            currentFavorite.push(article);
        }
        setFavorite(currentFavorite)
        localStorage.setItem('favorite', favorite);

    }



    const addToCart = (card) => {
        const index = cart.findIndex(el => el.article === card.article)
        const currentCart = cart;



        if (index === -1) {
            card.count = 1
            currentCart.push(card)
            setCart(currentCart)
        } else {
            currentCart[index].count += 1;

        }

        const newCartCount = currentCart.reduce((sum, current) => sum + current.count, 0);
        setCartCount(newCartCount)

        localStorage.setItem("cart", JSON.stringify(currentCart));
        localStorage.setItem("cartCount", JSON.stringify(newCartCount));
    }


    useEffect(() => {
        const getData = async () => {
            const data = await fetch("./data.json").then(res => res.json())
            setData(data)
        }
        getData()
    }, []);

    return (
        <div className="App">
            <Header cartCount={cartCount} />
            <CardList setModalProps={setModalProps} setIsModalActive={setIsModalActive} data={data} />
            {isModalActive && <Modal addToCart={addToCart} setIsModalActive={setIsModalActive} modalProps={modalProps} />}
        </div>
    );
}

export default App;
