import React from 'react';
import Button from '../Button/Button';

const CartItem = (props) => {

    const { setModalProps, setIsModalActive, removeFromCart, increment, dicrement, article, src, header, count, } = props

    const openModal = () => {
        const modalProps = {
            title: `Delite from cart ${header}?`, closeButton: true, btn: {
                btnTitle: 'Delite',
                onClick: removeFromCart,
            }
        }
        setModalProps(modalProps)
        setIsModalActive(true)
    }


    return (

        <li className='cart__item' >
            <img src={src} alt={header} />
            <h2 className='cart__header' >{header}</h2>
            <span className='cart__count' >{count}</span>
            <Button handleClick={() => { dicrement(article) }} >+</Button>
            <Button handleClick={() => { increment(article) }}>-</Button>
            <Button handleClick={() => { openModal() }}>DEL</Button>

        </li>


    );
}

export default CartItem;
