import './App.css';
import Header from './components/Header/Header';
import React, { useEffect, useState } from 'react';
import CardList from './components/CardList/CardList';
import Modal from './components/Modal/Modal';



function App() {

    const [data, setData] = useState([])
    const [isModalActive, setIsModalActive] = useState(false)
    const [modalProps, setModalProps] = useState({})
    const [cart, setCart] = useState(localStorage.getItem("cart") || [])
    const [favorite, setFavorite] = useState(localStorage.getItem("favorite") || [])



    useEffect(() => {
        const getData = async () => {
            const data = await fetch("./data.json").then(res => res.json())
            setData(data)
        }
        getData()
    }, []);

    return (
        <div className="App">
            <Header />
            <CardList setModalProps={setModalProps} setIsModalActive={setIsModalActive} data={data} />
            {isModalActive && <Modal setIsModalActive={setIsModalActive} modalProps={modalProps} />}
        </div>
    );
}

export default App;
