import React from 'react';
import CartItem from '../CartItem/CartItem';

const CartList = (props) => {

    const { removeFromCart, increment, cart, dicrement } = props

    return (
        <saction>
            <ul className='cart-list'>
                {cart.map(({ src, header, count, article }) => <CartItem removeFromCart={removeFromCart} increment={increment} article={article} dicrement={dicrement} src={src} header={header} count={count} />)}
            </ul>
        </saction>
    );
}

export default CartList;
