import React from 'react';
import "./Modal.scss"
import PropTypes from "prop-types";
import Button from '../Button/Button';


const Modal = (props) => {

    const { modalProps, setIsModalActive, addToCart } = props
    const { title, closeButton, card } = modalProps



    const closeModal = () => {
        setIsModalActive(false)
    }
    return (
        <>
            <div
                className='modal'
                onClick={(e) => {
                    if (e.target.className === "modal") {
                        closeModal();
                    }
                }}
            >
                <div className='modal__wrapper' >
                    {closeButton ? (
                        <button onClick={closeModal} className={"modal__close-btn"}>
                            &times;
                        </button>
                    ) : (
                        ""
                    )}
                    <h2 className='modal__title' >Add to cart {title}</h2>
                    <p className='modal__text' >Confirm your actions</p>
                    <div className='modal__btn-container'>
                        <Button handleClick={() => { addToCart(card); closeModal(); }} >Add to cart</Button>
                        <Button handleClick={closeModal} >Cancel</Button>

                    </div>
                </div>
            </div>
        </>)
}


Modal.propTypes = {
    modalProps: PropTypes.object.isRequired,
    setIsModalActive: PropTypes.func.isRequired,
    addToCart: PropTypes.func.isRequired
}

export default Modal
