import { CLOSE_MODAL, ADD_MODAL_PROPS } from "./actions"

export const closeModalAC = (payload) => ({ type: CLOSE_MODAL, payload })
export const addModalPropsAC = (payload) => ({ type: ADD_MODAL_PROPS, payload })
