import { SET_ISMODAL_ACTIVE, ADD_MODAL_PROPS } from "./actions"

export const closeModalAC = (payload) => ({ type: SET_ISMODAL_ACTIVE, payload })
export const addModalPropsAC = (payload) => ({ type: ADD_MODAL_PROPS, payload })
