import React, { useState } from 'react';
import "./Card.scss"
import Button from '../Button/Button';
import PropTypes from "prop-types";




const Card = (props) => {

    const { card, setModalProps, setIsModalActive } = props;
    const { header, price, article, src, color } = card

    const [isFavorite, setIsFavorite] = useState(false);

    const openModal = () => { setIsModalActive(true); setModalProps({ title: header, closeButton: true, card: card }); }
    const addToFavorite = () => { setIsFavorite(!isFavorite) }


    return (
        <li className='card' >
            <h2 className='card__header'>{header}</h2>
            <img src={src} alt={header} width={250} height={250} />
            <div className="card__info">
                <span className='card__prise' >{price}</span>
                <span className='card__article' >art:{article}</span>
                <span className='card__color' style={{ backgroundColor: color }}></span>
                <svg onClick={addToFavorite} className={isFavorite ? "card__favorite-svg--on" : "card__favorite-svg--off"} xmlns="http://www.w3.org/2000/svg" width="30" height="30" viewBox="0 0 24 24">
                    <path fill='#ffaa00'
                        d="M12 .587l3.668 7.568 8.332 1.151-6.064 5.828 1.48 8.279-7.416-3.967-7.417 3.967 1.481-8.279-6.064-5.828 8.332-1.151z" />
                </svg>
            </div>
            <Button handleClick={openModal} >Add to Cart</Button>
        </li>
    )
}

Card.propTypes = {
    card: PropTypes.object.isRequired,

}

export default Card
